import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/Addons.js';

const scene = new THREE.Scene();
let canvas = document.querySelector('#solar3d')

const camera = new THREE.PerspectiveCamera(70, window.innerWidth / window.innerHeight, 0.1, 5000);
camera.position.set(0, 400, 1000);


const renderer = new THREE.WebGLRenderer({canvas, alpha:true ,antialias: true });
renderer.setSize(window.innerWidth, window.innerHeight);
renderer.shadowMap.enabled = true;



const controls = new OrbitControls(camera, renderer.domElement);
controls.enableZoom = false;
controls.enableDamping = true;
controls.enableRotate = true;
controls.enablePan = false;

controls.touches = { ONE: THREE.TOUCH.NONE, TWO: THREE.TOUCH.NONE };
renderer.domElement.style.touchAction = 'pan-y';

// Starfield Background ðŸŒŒ
const starGeo = new THREE.SphereGeometry(4000, 64, 64);
const starMat = new THREE.MeshBasicMaterial({
  map: new THREE.TextureLoader().load('https://threejs.org/examples/textures/space.jpg'),
  side: THREE.BackSide
});
const starfield = new THREE.Mesh(starGeo, starMat);
scene.add(starfield);

// Lights ðŸ’¡
const ambient = new THREE.AmbientLight(0xffffff, 2);
scene.add(ambient);

const sunTexture = new THREE.TextureLoader().load('data:image/jpeg;base64,');
const sunMaterial = new THREE.MeshStandardMaterial({
  map: sunTexture,
  emissiveIntensity: 2,
});

let dynamicRadius = window.innerWidth < 780 ? 200:300
const sun = new THREE.Mesh(new THREE.SphereGeometry(dynamicRadius, 100, 100), sunMaterial);
scene.add(sun);

const sunLight = new THREE.PointLight(0xffffff, 30, 3000);
sunLight.castShadow = true;
scene.add(sunLight);

// Planet Factory ðŸŒ
function createPlanet(size, textureUrl, distance, speed, orbitColor) {
  const texture = new THREE.TextureLoader().load(textureUrl);
  const material = new THREE.MeshStandardMaterial({
    map: texture,
    roughness: 10,
    metalness: .05
  });

  const textureLoader = new THREE.TextureLoader();


  const planet = new THREE.Mesh(new THREE.SphereGeometry(size, 32, 32), material);

  const pivot = new THREE.Object3D();
  pivot.add(planet);
  planet.position.x = distance;
  scene.add(pivot);



  // Orbit circle
  const orbitGeo = new THREE.RingGeometry(distance - 0.2, distance + 0.2, 128);
  const orbitMat = new THREE.MeshBasicMaterial({
    color: orbitColor,
    side: THREE.DoubleSide,
    transparent: true,
    opacity: 0.25
  });
  const orbit = new THREE.Mesh(orbitGeo, orbitMat);
  orbit.rotation.x = Math.PI / 2;
  scene.add(orbit);

  return { planet, pivot, speed };
}

// Smaller sizes + spaced out distances
const planets = [
  createPlanet(20, 'data:image/jpeg;base64,', 400, 0.02, 0xaaaaaa),
  createPlanet(30, 'data:image/jpeg;base64,', 500, 0.015, 0xffcc66),
  createPlanet(45, 'https://threejs.org/examples/textures/planets/earth_atmos_2048.jpg', 600, 0.012, 0x3399ff),
  createPlanet(25, 'data:image/jpeg;base64,', 700, 0.010, 0xff3300),
  createPlanet(100, 'data:image/jpeg;base64,', 800, 0.008, 0xff9966),
  createPlanet(50, 'data:image/jpeg;base64,', 900, 0.006, 0xffcc99),
  createPlanet(30, 'data:image/jpeg;base64,', 1000, 0.004, 0x66ffff),
  createPlanet(30, 'data:image/jpeg;base64,', 1100, 0.003, 0x3366ff)
];

planets.forEach(planet => {
  console.log(planet);

})


// Animate ðŸš€
function animate() {
  requestAnimationFrame(animate);
  
  controls.update();

  sun.rotation.y += 0.002;

  planets.forEach(obj => {
    obj.pivot.rotation.y += obj.speed;
    obj.planet.rotation.y += 0.01;
  });

  renderer.render(scene, camera);
}
animate();

// Resize
window.addEventListener("resize", () => {
  camera.aspect = window.innerWidth / window.innerHeight;
  camera.updateProjectionMatrix();
  renderer.setSize(window.innerWidth, window.innerHeight);
});
